import { StatusBar } from 'expo-status-bar';
import { KeyboardAvoidingView, StyleSheet, Text, TextInput, View } from 'react-native';
import Task from './components/Task.js';

export default function App() {
  return (
    <View style={styles.container}>

      <View style={styles.taskWrapper}>

        <Text style={styles.sectionTitle} >Tarefas De hoje</Text>

        <View style={styles.itens}>
          <Task text={'teste 1'}></Task>
          <Task text={'teste 2'}></Task>
          <Task text={'teste 3'}></Task>
          <Task text={'teste 4'}></Task>
        </View>

      </View>

      <KeyboardAvoidingView behavior={Plataform.OS === "ios" ? "padding" : "height"}
        style={styles.writeTaskWrapper} >
        <TextInput style={styles.input} placeholder={'Escreva uma tarefa'} />
      </KeyboardAvoidingView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  taskWrapper: {
    paddingTop: 80,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  itens: {
    marginTop: 12,
  },
});
